@media (max-width: 768px) {
  .history-stats-grid {
    grid-template-columns: 1fr;
  }

  .history-filter-container {
    flex-direction: column;
  }

  .history-filter-container .form-group {
    min-width: 100%;
  }

  .distribution-bar {
    flex-wrap: wrap;
  }

  .bar-label,
  .bar-value {
    flex: 0 0 100%;
    text-align: left;
  }
}

