.tab-button:hover {
  color: var(--color-primary);
  background-color: var(--color-gray-100);
}

.tab-button.active {
  color: var(--color-primary);
  border-bottom-color: var(--color-primary);
  font-weight: 600;
  background-color: var(--color-white);
}

.tab-content {
  display: none;
}

.tab-content.active {
